package arrayList;

import java.util.ArrayList;
import java.util.Collections;

public class UtilidadesArrayList {
	
	static private ArrayList<Integer> aleatorios;
	
	public UtilidadesArrayList() {
		
	}
	
	static public void generaArrayListInt(int longitud, int min, int max) {
		
		aleatorios = new ArrayList<Integer>(longitud);
		
		for (int i =0;i<longitud;i++) {
			int aleatorio = (int)Math.random()*(max - min) + min;
			aleatorios.add(aleatorio);
		}
	}
	
	static public int minimoArrayListInt(ArrayList<Integer> al) {
		int minimo = Integer.MAX_VALUE;
		for (int n:al) {
			if (n < minimo) {
				minimo=n;
			}
		}
		return minimo;
			
	}
	
	static public int maximoArrayListInt(ArrayList<Integer> al) {
		int maximo = Integer.MIN_VALUE;
		for (int n:al) {
			if (n > maximo) {
				maximo=n;
			}
		}
		return maximo;			
	}
	static public double mediaArrayList(ArrayList<Integer> al) {
		double suma=0.0;
		for (int n:al) {
			suma += n;
		}
		return suma/al.size();
	}
	
	static public boolean estaEnArrayListInt(ArrayList<Integer> al, Integer num) {
		
		return al.contains(num);
	}
	
	static public int posicionEnArrayList(ArrayList<Integer> al, Integer num) {
		
		return al.indexOf(num);
	}
	
	static public ArrayList<Integer> volteaArrayListInt(ArrayList<Integer> al){
		
		Collections.reverse(al);
		return al;
	}
	
	static public void muestraDatosArrayList(ArrayList<Integer> al) {
		System.out.println(al);
	}
	
	
	

}

