package Banco;

import java.util.ArrayList;

public class Banco {
	
	private String direccion;
	private String pais_sede;
	ArrayList<CuentaBancaria> cb;
	
	public Banco(String pais_sede) {
		this.pais_sede=pais_sede;
		cb = new ArrayList<CuentaBancaria>();		
	}
	
	public void crearCuenta(double saldo,String titular,String tipoCuenta) {
		if (tipoCuenta.equalsIgnoreCase("CuentaCorriente")) {
			cb.add(new CuentaCorriente(saldo,titular));
		}else if (tipoCuenta.equalsIgnoreCase("CuentaAhorro")) {
			cb.add(new CuentaAhorro(saldo,titular));
		}else {
			System.out.println("Error no ha escrito correctamente el tipo de cuenta");
		}
		String iban = (cb.get(cb.size()-1)).getIban();
		System.out.println("El iban de la nueva cuenta creada es: "+iban);
	}

	public void muestraTodasLasCuentas() {
		
		for(CuentaBancaria c:cb) {
			System.out.println(c);
		}
		
	}

	public void consultaSaldo(String iban) {
		
		for(CuentaBancaria c:cb) {
			if (c.getIban().equals(iban)) {
				
				System.out.println("El saldo de esta cuenta es: "+c.getSaldo());
			}
		}
		
	}

	public void retirarSaldo(String iban, double saldo_retirar) {
		for(CuentaBancaria c:cb) {
			if (c.getIban().equals(iban)) {
				
				if (c.retirarSaldo(saldo_retirar)) {
					System.out.println("Saldo retirado correctamente su saldo actual es: "+c.getSaldo());
				}else {
					System.out.println("No se pudo retirar..");
				}
			}
		}
		
	}

	public void ingresar(String iban, double cantidad) {
		for(CuentaBancaria c:cb) {
			if (c.getIban().equals(iban)) {
				
				c.ingresar(cantidad);
			}
		}
		
	}

	public void transferir(String iban_a, String iban_b, double cantidad) {
		
		CuentaBancaria cuenta_a = null;
		CuentaBancaria cuenta_b = null;
		
		for(CuentaBancaria c:cb) {
			if (c.getIban().equals(iban_a)) {
				cuenta_a = c;
			}
			if (c.getIban().equals(iban_b)) {
				cuenta_b = c;
			}			
		}
		
		cuenta_a.transfiere(cuenta_b,cantidad);
		
	}
}

