package Banco;

public class CuentaAhorro extends CuentaBancaria{

	private static final double SALDO_MINIMO = 50;
	
	public CuentaAhorro(double saldo, String titular) {
		super(saldo,titular);
	}
	
	public double calcularIntereses() {
		if (this.getSaldo() < SALDO_MINIMO) {
			return (this.getSaldo()*CuentaBancaria.interesAnualBasico)/2;
		}else {
			return (this.getSaldo()*CuentaBancaria.interesAnualBasico)*2;
		}
	}
	
	@Override
	public String toString() {
		return "CuentaAhorro [saldo=" + this.getSaldo() + ", iban=" + this.getIban() + ", titular=" + this.getTitular() + "]";
	}
}

==> ./CuentaBancaria.java <==
package Banco;

public abstract class CuentaBancaria{
	
	private static final String codigo_iban = "ESP";
	private static Integer numero_iban = 0;
	protected static final double interesAnualBasico = 0.01;//1%
	private double saldo;
	private String iban;
	private String titular;
	
	public CuentaBancaria(double saldo,String titular) {
		this.saldo = saldo;
		this.titular = titular;
		iban = getCodigoIban();	
	}
	
	public abstract double calcularIntereses();
	
	private static String getCodigoIban() {
		numero_iban++;
		return codigo_iban + numero_iban.toString();
	}

	public double getSaldo() {
		return saldo;
	}

	public void setSaldo(double saldo) {
		this.saldo = saldo;
	}

	public String getIban() {
		return iban;
	}

	public void setIban(String iban) {
		this.iban = iban;
	}
	
	

	public String getTitular() {
		return titular;
	}

	public void setTitular(String titular) {
		this.titular = titular;
	}

	@Override
	public String toString() {
		return "CuentaBancaria [saldo=" + saldo + ", iban=" + iban + ", titular=" + titular + "]";
	}
	
	public boolean retirarSaldo(double cantidad) {
		if ((this.saldo - cantidad) < 0) {
			System.out.println("Lo siento no hay suficiente..");
			return false;
		}else {
			this.saldo= this.saldo - cantidad;
			return true;
		}
	}

	public void ingresar(double cantidad) {
		this.saldo += cantidad;
		
	}
	
	public void transfiere(CuentaBancaria cuenta_b,double cantidad) {
		if (this.retirarSaldo(cantidad)) {
			cuenta_b.ingresar(cantidad);
			System.out.println("La trasferencia se ha realizado correctamente");
		}else {
			System.out.println("No se ha podido realizar la operación");
		}
		
	}
	
	

}

