package Banco;

import javax.swing.JOptionPane;

public class Main {

	static Banco banco = new Banco("Francia");
	
	public static void main(String[] args) {		
		
		int opcion;
		do {
			System.out.println("Indique qué opción desea: "
					+ "\n1. Introducir una nueva cuenta bancaria"					
					+ "\n2. Mostrar todas las cuentas bancarias"
					+ "\n3. Consultar saldo"
					+ "\n4. Retirar"
					+ "\n5. Ingresar"
					+ "\n6. Transferir"
					+ "\n7. Salir del Menú");
			opcion = Integer.parseInt(JOptionPane.showInputDialog("Introduzca Opción","opción")); 
			if (opcion != 7) {
				menu(opcion);
			}			
			
		}while(opcion != 7);
		System.out.println("Ciao");
	}
	
	public static  void menu(int op) {
		
		switch (op){
			case 1:				
				crearNuevaCuenta();						
				break;			
			case 2:
				mostrarTodasCuentasBancarias();
				break;
			case 3:
				consultarSaldo();
				break;
			case 4:
				retirar();				
				break;
			case 5:
				ingresar();				
				break;
			case 6:
				transferirFondos();
				break;				
			default:
				System.out.println("ERROR OPCIÓN EQUIVOCADA..");			
		
		}
	}

	private static void transferirFondos() {
		String iban_a = JOptionPane.showInputDialog("Introduzca el IBAN de la cuenta A","IBAN");
		double cantidad = Double.parseDouble(JOptionPane.showInputDialog("Introduzca la cantidad a transferir","cantidad"));
		String iban_b = JOptionPane.showInputDialog("Introduzca el IBAN de la cuenta B","IBAN");
		banco.transferir(iban_a,iban_b,cantidad);
		
	}

	private static void ingresar() {
		String iban = JOptionPane.showInputDialog("Introduzca el IBAN","IBAN");
		double cantidad = Double.parseDouble(JOptionPane.showInputDialog("Introduzca la cantidad a ingresar","cantidad"));
		banco.ingresar(iban,cantidad);
	}

	private static void retirar() {
		String iban = JOptionPane.showInputDialog("Introduzca el IBAN","IBAN");
		double saldo_retirar = Double.parseDouble(JOptionPane.showInputDialog("Introduzca el saldo a retirar","Saldo"));
		banco.retirarSaldo(iban,saldo_retirar);
	}

	private static void consultarSaldo() {
		
		String iban = JOptionPane.showInputDialog("Introduzca el IBAN","IBAN");
		banco.consultaSaldo(iban);
	}

	private static void mostrarTodasCuentasBancarias() {
		banco.muestraTodasLasCuentas();
		
	}


	private static void crearNuevaCuenta() {
		System.out.println("Introduce el saldo de la nueva cuenta");
		String cantidad = JOptionPane.showInputDialog("Introduzca el saldo","saldo");
		double saldo = Double.parseDouble(cantidad);
		String titular = JOptionPane.showInputDialog("Escriba el nombre del titular","titular");
		System.out.println("Escribe qué tipo de cuenta: CuentaCorriente o CuentaAhorro");
		String tipoCuenta = JOptionPane.showInputDialog("Escriba el tipo de cuenta","tipo Cuenta");
		banco.crearCuenta(saldo,titular,tipoCuenta);		
	}

}

