package colecciones;

import java.util.ArrayList;

public class Main {

	public static void main(String[] args) {
		
		ArrayList<Producto> p = new ArrayList<Producto>();
		
		p.add(new Producto("Naranjas",20));
		p.add(new Producto("Peras",112));
		
		Producto prp=new Producto("Naranjas",20);
		
		System.out.println("Identificador objeto arraylist "+(p.get(0)));
		System.out.println("Identificador objeto prp "+prp);
		
