==> ./Producto.java <==
package colecciones;

public class Producto {
	
	private String nombre;
	private int cantidad;
	public Producto(String nomb,int cant) {
		this.nombre = nomb;
		this.cantidad = cant;
	}
	public String getNombre() {
		return nombre;
	}
	public void setNombre(String nombre) {
		this.nombre = nombre;
	}
	public int getCantidad() {
		return cantidad;
	}
	public void setCantidad(int cantidad) {
		this.cantidad = cantidad;
	}
	
	@Override
	public boolean equals(Object obj) {
		
		if ((obj != null) && (obj instanceof Producto)) {
			Producto p = (Producto)obj;
			//miramos si la cantidad  es la misma
			if (this.cantidad != p.getCantidad()) {
				return false;
			}else if (!this.nombre.equalsIgnoreCase(p.getNombre())) {
				return false;				
			}
			//si el objeto es null o no es un producto
		}else {
			return false;
		}		
		
		return true;
	}

}

