==> ./OrdenarCliente.java <==
package colecciones2Ordenar;

import java.util.Comparator;

public class OrdenarCliente implements Comparator{

	@Override
	public int compare(Object o1, Object o2) {
		if ((o1 instanceof Cliente) && (o2 instanceof Cliente)){
			Cliente a =(Cliente)o1;
			Cliente b =(Cliente)o2;
			//si queremos ordenar por edad
			//return a.getEdad() - b.getEdad();
			return a.getNombre().compareTo(b.getNombre());
		}
		return 0;
	}

}

