package coleccionhashtable;

import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.util.ArrayList;
import java.util.Collections;
import java.util.Enumeration;
import java.util.Hashtable;
import java.util.Random;

import javax.swing.JOptionPane;



public class Main {

	public static Hashtable<String,Estudiante> estudiantes;
	public static final String NOMB_FICH="Estudiantes.txt";
	public static void main(String[] args) {
		File f = new File(NOMB_FICH);
		if (!f.exists()){
			creaFichero();
		}
		int opcion;
		do {
			muestraMenu();
			opcion = Integer.parseInt(JOptionPane.showInputDialog("Introduzca una opción"));
			
			if (opcion !=0) {
				menu(opcion);
			}
			
		}while(opcion != 0);
		System.out.println("Hasta la vista");

	}
	private static void muestraMenu() {
		System.out.println("\nIntroduzca una opcion:"
				+ "\n0. Salir"
				+ "\n1. Cargar datos"
				+ "\n2. Mostrar todos los estudiantes"
				+ "\n3. Modificar estudiante"
				+ "\n4. Añadir estudiante"
				+ "\n5. Borrar estudiante"
				+ "\n6. Buscar estudiante"
				+ "\n7. Guardar estudiantes ordenados por apellido");
		
	}
	private static void menu(int opcion) {
		switch(opcion) {
			case 1:
				cargarDatos();
				break;
			case 2:
				mostrarDatos();
				break;
			case 3:
				modificarEstudiante();
				break;
			case 4:
				anadirEstudiante();
				break;
			case 5:
				borrarEstudiante();
				break;
			case 6:
				buscarEstudiante();
				break;
			case 7:
				guardarEstudiantesOrdenados();
				break;
			default:
				System.out.println("Opción equivocada");
		}
		
	}
	private static void guardarEstudiantesOrdenados() {
		//Collections.sort(estudiantes,new OrdenaEstudiante());
		
		try {
			BufferedWriter bw = new BufferedWriter(new FileWriter(NOMB_FICH));
			
			Enumeration claves = estudiantes.elements();
			while(claves.hasMoreElements()) {
				Estudiante e = (Estudiante) claves.nextElement();
				String linea=e.getDni()+":"+e.getNombre()+":"+e.getApellido()+":"+e.getEdad()+"\n";
				bw.write(linea);
				
			}
			
			bw.close();
		} catch (IOException e1) {
			// TODO Auto-generated catch block
			e1.printStackTrace();
		}
		
		
		
		
	}
	private static void buscarEstudiante() {
		String dni = JOptionPane.showInputDialog("Introduzca el dni del estudiante a buscar");
		if (estudiantes != null) {
			if (estudiantes.containsKey(dni)) {		
				System.out.println((estudiantes.get(dni)));
			}
		}
		
	}
	private static void borrarEstudiante() {
		String dni = JOptionPane.showInputDialog("Introduzca el dni del estudiatne a borrar");
		if (estudiantes != null) {
			if (estudiantes.containsKey(dni)) {
				estudiantes.remove(dni);				
			}
		}
		
	}
	private static void anadirEstudiante() {
		String dni = JOptionPane.showInputDialog("Introduzca el dni del nuevo estudiante ");
		String nombre = JOptionPane.showInputDialog("Introduzca el nombre del nuevo estudiante ");
		String apellidos = JOptionPane.showInputDialog("Introduzca el apellido del nuevo estudiante ");
		int edad = Integer.parseInt(JOptionPane.showInputDialog("Escriba la edad del nuevo estudiante"));
		Estudiante e = new Estudiante(dni,nombre,apellidos,edad);
		if (estudiantes != null) {
			if (!estudiantes.containsKey(dni)) {
				estudiantes.put(e.getDni(),e);
			}else {
				System.out.println("Lo siento ese dni ya está escogido");
			}
			
		}else {
			System.out.println("Se deben cargar datos primero...");
		}
		
	}
	private static void modificarEstudiante() {
		String dni = JOptionPane.showInputDialog("Introduzca el dni del estudiante");
		if (estudiantes.containsKey(dni)) {
			
			Estudiante e = estudiantes.get(dni);
			System.out.println("Indique qué desea modificar"
					+ "\n1. DNI"
					+ "\n2. Nombre"
					+ "\n3. Apellidos"
					+ "\n4. Edad");
			int campo= Integer.parseInt(JOptionPane.showInputDialog("Elija campo"));
			switch (campo) {
				case 1:
					String dnin = JOptionPane.showInputDialog("Introduzca el nuevo dni del estudiante");
					e.setDni(dnin);
					break;
				case 2:
					String nombren = JOptionPane.showInputDialog("Introduzca el nuevo nombre del estudiante");
					e.setNombre(nombren);
					break;
				case 3:
					String apellin = JOptionPane.showInputDialog("Introduzca el nuevo apellido del estudiante");
					e.setNombre(apellin);
					break;
				case 4:
					int edadn= Integer.parseInt(JOptionPane.showInputDialog("Introduzca la edad nueva"));
					e.setEdad(edadn);
					break;
				default:
					System.out.println("Campo equivocado");
			}	
			estudiantes.put(e.getDni(), e);			
		}
		
	}
	private static void mostrarDatos() {
		Enumeration claves = estudiantes.elements();
		while(claves.hasMoreElements()) {
			Estudiante e = (Estudiante) claves.nextElement();
			System.out.println(e);
		}		
	}
	private static void cargarDatos() {
		
		if (estudiantes == null) {
			estudiantes = new Hashtable<String,Estudiante>();
			try {
				BufferedReader br = new BufferedReader(new FileReader(NOMB_FICH));
				String linea;
				while ((linea = br.readLine())!=null) {
					String []datos =linea.split(":");
					Estudiante e = new Estudiante(datos[0],datos[1],datos[2],Integer.parseInt(datos[3]));
					if (!estudiantes.containsKey(e.getDni())){
						estudiantes.put(e.getDni(),e);
					}					
				}
				br.close();
			
			} catch (FileNotFoundException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			} catch (IOException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}else {
			System.out.println("Los estudiantes ya estan cargados");
		}
		
	}
	private static void creaFichero() {
		Integer n=1;
		String []apellidos = {"Lopez","Perez","Martinez","Garcia","Gonzalez","Plasencia","Sempere"};
		Random r = new Random();
		try {
			BufferedWriter bw= new BufferedWriter(new FileWriter(NOMB_FICH));
			
			while (n<20) {
				String dni = n.toString()+n.toString()+n.toString()+"k";
				String nombre = "Alumno"+n;
				String apell = apellidos[r.nextInt(apellidos.length)];
				int edad = r.nextInt(101);
				String linea = dni+":"+nombre+":"+apell+":"+edad;
				bw.write(linea+"\n");
				n++;
			}
			bw.close();
			
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}

}

