package ejercicio2;

import java.util.ArrayList;
import javax.swing.*;

public class Main {

	private static ArrayList<Double> al = new ArrayList<Double>();
	public static void main(String[] args) {
		boolean seguir = true;
		double sumas=0.0, mayor=Double.MIN_VALUE,menor=Double.MAX_VALUE,media=0.0;
		while (seguir) {
			double altura = Double.parseDouble(JOptionPane.showInputDialog("Introduzca altura" ));
			
			if (altura == -1) {
				seguir = false;
			}else {
				if (altura > mayor) {
					mayor = altura;
				}
				if (altura < menor) {
					menor = altura;
				}
				sumas = sumas + altura;
				al.add(altura);
			}
		}
		
		media = media/al.size();
		System.out.println("La menor altura es: "+menor);
		System.out.println("La mayor altura es: "+mayor);
		System.out.println("La media es: "+ media);
		System.out.println("El numero de alturas por encima de la media es: "+alturasEncimaMedia(al,media));
		System.out.println("El numero de alturas por debajo de la media es: "+alturasDebajoMedia(al,media));
		

	}
	private static String alturasDebajoMedia(ArrayList<Double> al2, double media) {
		Integer num=0;
		for (double d:al) {
			if (d>media) {
				num++;
			}
		}
		return num.toString(); 
		
	}
	private static String alturasEncimaMedia(ArrayList<Double> al2,double media) {
		Integer num=0;
		for (double d:al) {
			if (d<media) {
				num++;
			}
		}
		return num.toString(); 
	}
	

}

