package ejercicio3;

import java.util.ArrayList;

import javax.swing.JOptionPane;

public class Main {

	private static ArrayList<Integer> al=new ArrayList<Integer>();
	
	public static void main(String[] args) {
		int opcion;
		do {
			System.out.println("1. Mostrar valores"
					+ "\n2. Introducir valor al final"
					+ "\n3. Introducir valor en una posición"
					+ "\n4. Salir");
			opcion = Integer.parseInt(JOptionPane.showInputDialog("Introduzca una opción"));
			if (opcion != 4) {
				menu(opcion);
			}
			
		}while(opcion != 4);

	}

	private static void menu(int opcion) {
		
		switch(opcion){
			case 1:
				mostrarValores();
				break;
			case 2:
				int valor = Integer.parseInt(JOptionPane.showInputDialog("Introduzca el valor a colocar al final"));
				introducirValorFinal(valor);
				break;
			case 3:
				int valorc = Integer.parseInt(JOptionPane.showInputDialog("Introduzca el valor a colocar "));
				int pos = Integer.parseInt(JOptionPane.showInputDialog("Introduzca la posición "));
				introducirValorPosicion(valorc,pos);
				break;
			default:
				System.out.println("valor incorrecto..");				
		}
		
	}

	private static void introducirValorPosicion(int valorc, int pos) {
		
		al.add(pos,valorc);
		
	}

	private static void introducirValorFinal(int valor) {
		al.add(valor);		
	}

	private static void mostrarValores() {
		System.out.println(al);		
	}

}

