package electrodomesticos;

public class Electrodomestico {
	
	//constantes
	
	protected final String COLOR_DEF = "blanco";
	protected final char CONSUMO_ENERGETICO_DEF = 'F';
	protected final double PRECIO_BASE_DEF = 100;
	protected final double PESO_DEF = 5;
	
	//atributos
	
	protected String color;
	protected char consumo;
	protected double precioBase;
	protected double peso;
	
	public Electrodomestico() {
		this.color=COLOR_DEF;
		this.consumo = CONSUMO_ENERGETICO_DEF;
		this.precioBase=PRECIO_BASE_DEF;
		this.peso = PESO_DEF;
	}
	
	public Electrodomestico(double prec,double pes) {
		this();//llamo al constructor base
		this.precioBase = prec;
		this.peso = peso;
	}

	public Electrodomestico(String color, char consumo, double precioBase, double peso) {		
		this.setColor(color);	
		this.setConsumo(consumo);
		this.precioBase = precioBase;
		this.peso = peso;
	}

	public String getColor() {
		return color;
	}

	public void setColor(String color) {
		if (this.comprobarColor(color)) {
			this.color = color;
		}else {
			this.color = COLOR_DEF;
		}
		
	}

	public char getConsumo() {
		return consumo;
	}

	public void setConsumo(char consumo) {
		if (this.comprobarConsumoEnergetico(consumo)) {
			this.consumo = consumo;
		}else {
			this.consumo = CONSUMO_ENERGETICO_DEF;
		}
		
	}

	public double getPrecioBase() {
		return precioBase;
	}

	public void setPrecioBase(double precioBase) {
		this.precioBase = precioBase;
	}

	public double getPeso() {
		return peso;
	}

	public void setPeso(double peso) {
		this.peso = peso;
	}
	
	private boolean comprobarConsumoEnergetico(char letra) {
		if ((letra >= 'A') && (letra <= 'F')) {
			return true;
		}else {
			return true;
		}		
	}
	
	private boolean comprobarColor(String color) {
		String [] colores = {"blanco","negro","rojo","azul","gris"};
		boolean encontrado=false;
		for (int i=0; i < colores.length && !encontrado;i++) {
			if (colores[i].equalsIgnoreCase(color)) {
				encontrado = true;			
			}
		}
		if (!encontrado) {
			return false;
		}else {
			return true;
		}			
	}
	
	public double precioFinal() {
		double plus = 0.0;
		switch(this.consumo) {
			case 'A':
				plus += 100;
				break;
			case 'B':
				plus += 80;
				break;
			case 'C':
				plus += 60;
				break;
			case 'D':
				plus += 50;
				break;
			case 'E':
				plus += 30;
				break;
			case 'F':
				plus += 10;
				break;
		}
		if ((this.peso >= 0)&&(this.peso<=19)){
			plus += 10;
		}
		else if ((this.peso >= 20)&&(this.peso<=49)){
			plus += 50;
		}
		else if ((this.peso >= 50)&&(this.peso<=79)){
			plus += 80;
		}else if (this.peso >= 80){
			plus += 100;
		}
		
		return this.precioBase + plus;
		
	}
 	
	
	

}

