	private final double CARGA_DEF = 5;
	
	//atributos
	private double carga;
	
	public Lavadora() {
		super();
		this.carga = CARGA_DEF;
	}
	/**
	 * 
	 * @param prec
	 * @param peso
	 */
	public Lavadora(double prec, double peso) {
		super(prec,peso);//esta llamada tb llama al constructor por defecto de electrodomestico
		this.carga=CARGA_DEF;
	}
	
	/**
	 * 	
	 * @param carga
	 * @param color
	 * @param consumo
	 * @param precioBase
	 * @param peso
	 */
	public Lavadora(double carga, String color, char consumo, double precioBase, double peso) {
		super(color,consumo,precioBase,peso);
		this.carga=carga;
	}
	public double getCarga() {
		return carga;
	}
	
	@Override
	public double precioFinal() {
		double preciof = super.precioFinal();
		if (this.carga >= 30) {
			return preciof + 50;
		}else {
			return preciof;
		}
		
	}
	
}

