package electrodomesticos;

public class Television extends Electrodomestico {
	
	//constantes
	private final double RESOLUCION_DEF = 20;
	private final boolean SINTONIZADOR_DEF = false;
	
	//atributos
	private double resolucion;
	private boolean sintonizador;
	
	//constructor
	public Television() {
		super();
		this.resolucion=RESOLUCION_DEF;
		this.sintonizador=SINTONIZADOR_DEF;
	}
	
	public Television(double preci, double peso) {
		super(preci,peso);
		this.resolucion=RESOLUCION_DEF;
		this.sintonizador=SINTONIZADOR_DEF;
	}

	public Television(double resolu, boolean sinto, String color, char consumo, double precioBase, double peso) {
		super(color, consumo, precioBase, peso);
		this.resolucion=resolu;
		this.sintonizador=sinto;		
	}

	public double getResolucion() {
		return resolucion;
	}

	public boolean isSintonizador() {
		return sintonizador;
	}

	@Override
	public double precioFinal() {		
		double preciof = super.precioFinal();
		if (this.resolucion > 40) {
			preciof = preciof * 1.3;
		}
		if (this.sintonizador) {
			preciof += 50;
		}
		return preciof;
	}
	
	

}

