package ficheros;
import java.io.*;
import java.util.ArrayList;
public class Main {

	public static void main(String[] args) {
		
		/* Para leer ficheros de texto utilizaremos la clase
		 * BufferedReader que tiene un constructor que necesita 
		 * una instancia de la clase FileReader
		 * 	
		 * */
		ArrayList<Transporte> t = new ArrayList<Transporte>();
		BufferedReader br;
		FileReader fr;
		boolean fallo=false;
		try{
			//la línea de abajo si falla haría saltar el bloque FileNotFoundException
			fr = new FileReader("C:\\Users\\Pedro Pablo\\Documents\\f.txt");
			br = new BufferedReader(fr);
			String linea;
			//la linea de abajo haría saltar el bloque IOException (si falla)
			while ((linea = br.readLine()) != null) {
//String tipo, String color, double peso, double combustible) {
				String []datos = linea.split(":");
				Transporte tp=new Transporte(datos[0],datos[1],Double.parseDouble(datos[2]),(Double.parseDouble(datos[3]))+200);
				t.add(tp);
				//System.out.println(t.get(t.size()).toString());				
			}
			br.close();
			
			escribeArrayList("C:\\Users\\Pedro Pablo\\Documents\\f.txt",t);
			//System.out.println(t);
			//la linea de abajo haría saltar el bloque Exception
			//System.out.println((4/0));
			
			
		}catch(FileNotFoundException fne) {
			fallo=true;
			System.out.println(fne.getMessage());
			System.out.println("uno");
		}catch(IOException ex) {
			fallo= true;
			System.out.println("dos");
			System.out.println(ex.getMessage());
		}catch(Exception e) {
			fallo= true;
			System.out.println("tres");
			System.out.println(e.getMessage());
		}finally {
			//este bloque se ejecutará sí o sí tanto si falla como si no
			if (fallo) {
				System.out.println("El programa no funcionó correctamente");
			}else {
				System.out.println("Programa finalizado correctamente");
			}
		}
	}

	private static void escribeArrayList(String nombfich, ArrayList<Transporte> t) {
		
		
		try {
			BufferedWriter bw = new BufferedWriter(new FileWriter(nombfich));
			for (Transporte tp:t) {
				System.out.println(tp.toString());
				bw.write(tp.toString()+"\n");
			}
			bw.close();
		
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
	}

}

==> ./Transporte.java <==
package ficheros;

public class Transporte {
	private String tipo;
	private String color;
	private double peso;
	private double combustible;
	public Transporte(String tipo, String color, double peso, double combustible) {
		super();
		this.tipo = tipo;
		this.color = color;
		this.peso = peso;
		this.combustible = combustible;
	}
	public String getTipo() {
		return tipo;
	}
	public void setTipo(String tipo) {
		this.tipo = tipo;
	}
	public String getColor() {
		return color;
	}
	public void setColor(String color) {
		this.color = color;
	}
	public double getPeso() {
		return peso;
	}
	public void setPeso(double peso) {
		this.peso = peso;
	}
	public double getCombustible() {
		return combustible;
	}
	public void setCombustible(double combustible) {
		this.combustible = combustible;
	}
	
	public String toString() {
		return this.tipo+":"+this.color+":"+this.peso+":"+this.combustible;
	}
	
}
